    if (val.length === 0) {
      activeHeadings.value = oldVal;
    } else {
      activeHeadings.value = val;
    }
  });
  onBeforeMount(() => observer.value = new IntersectionObserver(observerCallback));
  onBeforeUnmount(() => observer.value?.disconnect());
